import React, { useState } from "react";
import data from "../../json/productsData";
import Products from "./products";
import "./products.scss";
import Summary from "./summary";
import summary from "./summary";

const Main = () => {
  const [products, setProducts] = useState(data);
  const [cartItems, setCartItems] = useState([]);
  const addToCart = (e) => {
    const isExist = cartItems.find((data) => data.id === e.target.id);
    console.log(Boolean(e.target.dataset.remove));
    !isExist &&
      products.products.map((data) => {
        if (e.target.id === data.id) {
          return setCartItems([...cartItems, data]);
        }
      });

    isExist &&
      setCartItems(
        cartItems.map((item) => {
          if (e.target.id === item.id) {
            return { ...item, qty: item.qty + 1 };
          } else {
            return item;
          }
        })
      );

    isExist &&
      e.target.dataset.remove &&
      setCartItems(
        cartItems.map((item) => {
          if (e.target.id === item.id && item.qty !== 1) {
            return { ...item, qty: item.qty - 1 };
          } else {
            return item;
          }
        })
      );
  };
  console.log(cartItems);
  return (
    <div>
      <div className="products-info">
        <Products
          products={products}
          setCartItems={setCartItems}
          addToCart={addToCart}
        />
        <Summary cartItems={cartItems} addToCart={addToCart} />
      </div>
    </div>
  );
};

export default Main;
