import React, { useState } from "react";
import OverlayLink from "./overlayLink";
import "./overlay.scss";

const Overlay = () => {
  const [open, setOpen] = useState(false);
  const openOverlay = () => {
    setOpen(true);
  };
  const closeOverlay = () => {
    setOpen(false);
  };
  return <OverlayLink setOpen={setOpen} />;
};

export default Overlay;
