import React from "react";

const Summary = ({ cartItems, addToCart }) => {
  const cartPrice = cartItems.reduce((a, c) => a + c.price * c.qty, 0);
  const taxPrice = cartPrice * 0.1;
  return (
    <div className="summary-wrapper">
      <div className="cart-items">Cart Items</div>
      {cartItems &&
        cartItems.map((data, i) => {
          return (
            <div className="cart-items-info" key={i}>
              <div>{data.name}</div>
              <div className="cart-item-buttons">
                <button
                  className="button animation"
                  id={data.id}
                  onClick={(e) => addToCart(e)}
                >
                  +
                </button>
                <button
                  className="button animation"
                  id={data.id}
                  onClick={(e) => addToCart(e)}
                  data-remove="remove"
                >
                  -
                </button>
              </div>
              <div>{`${data.qty} x ${data.price}`}</div>
            </div>
          );
        })}
      {cartItems.length ? (
        <div className="summary">
          <div className="summary-title">Summary</div>
          <div className="border" />

          <div className="item-summary">
            <div>Item Price</div>
            <div>${cartPrice}</div>
          </div>
        </div>
      ) : (
        ""
      )}
    </div>
  );
};

export default Summary;
