import React from "react";

const reducer = (state, action) => {
  console.log(state);
  switch (action.type) {
    case "add":
      return { count: state.count + 1, payLoad: { ...action } };
    case "sub":
      return { count: state.count - 1 };
    case "reset":
      return { count: 0 };
    default:
      break;
  }
};

export default reducer;
