import React, { useState, useEffect } from "react";
import "../navBar/navBar.scss";
import TextFeild from "../textField/textField";

const NavBarSearch = ({ products, setProducts }) => {
  //   console.log(products);
  const [input, setInput] = useState("");
  const [filter, setFilter] = useState("");
  //   useEffect(() => {
  //     products.filter((product) => {
  //       //   debugger;
  //       if (input !== "" && product.name.startsWith(input)) {
  //         return setFilter(product);
  //       }
  //     });
  //   }, [input]);

  const getFilterList = (e) => {
    setInput(e.target.value);
    const filtered = products.filter((product) => {
      console.log(e.target.value, product.name.toLowerCase(), "filter");
      if (product.name.toLowerCase().startsWith(e.target.value.toLowerCase())) {
        return true;
      } else if (e.target.value === "") {
        return products;
      }
    });
    setProducts(filtered);
  };
  //   console.log(filter);
  return (
    <div className="container fade-in products-search">
      <div className="dropdown-container">
        <ul className="dropdown-list" id="nav-list">
          <a>
            <li id="home">Home</li>
          </a>
          <a>
            <li id="news">News</li>
          </a>
          <input type="text" value={input} onChange={(e) => getFilterList(e)} />
          {/* <TextFeild
            onBlur={(e) => null}
            setInput={setInput}
            input={input}
            inputAttrs={inputAttrs}
          /> */}
          <a>
            <li id="login">Login</li>
          </a>
        </ul>
      </div>
    </div>
  );
};

export default NavBarSearch;
