import React, { useState } from "react";
import TextField from "../textField/textField";
import Button from "../button/button";
import "./login.scss";

const Login = () => {
  const [firstName, setfirstName] = useState("");
  const [lastName, setlastName] = useState("");
  const [emailVaild, setEmailVaild] = useState(false);
  const [passwordValid, setPasswordValid] = useState(false);
  const onBlur = (e) => {
    if (e.target.value === "") {
      //   debugger;
      setPasswordValid(true);
    } else {
      setPasswordValid(false);
    }
  };

  const emailValid = (e) => {
    const pattern = new RegExp(
      /^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i
    );
    if (
      (e.target.id =
        "email" && e.target.value !== "" && pattern.test(e.target.value))
    ) {
      setEmailVaild({ ...error, [e.target.id]: false });
    } else if (e.target.value === "") {
      setEmailVaild({ ...error, [e.target.id]: true });
    }
  };
  return (
    <div className="login-wrapper">
      <div className="login-fields">
        <div className="login-title">Enter Login Details</div>
        <form action="">
          <TextField
            inputLabel="Email"
            id="email"
            setInput={setfirstName}
            input={firstName}
            onBlur={onBlur}
            error={error.email}
            errorMessage="Please enter valid email"
          />
          <TextField
            id="password"
            inputLabel="Password"
            setInput={setlastName}
            input={lastName}
            onBlur={onBlur}
            error={error.password}
            errorMessage="Please enter valid email"
          />
        </form>

        <div className="login-buttons">
          <Button label="Login" />
          <Button buttonLink label="Forgot Password" />
        </div>
      </div>
    </div>
  );
};

export default Login;
