import React from "react";
import LeftScroll from "./LeftScroll";
import RightScroll from "./RightScroll";
import "./syncScroll.scss";

const SyncScroll = () => {
  const syncScrol = (scroll) => {
    const face1 = document.getElementById("scroll1");
    const face2 = document.getElementById("scroll2");
    if (scroll == "scroll1") {
      face2.scrollTop = face1.scrollTop;
    } else {
      face1.scrollTop = face2.scrollTop;
    }
  };
  return (
    <div className="parent-scroll">
      <LeftScroll />
      <RightScroll />
    </div>
  );
};

export default SyncScroll;
