import React, { useState } from "react";

import OverlayContent from "./overlayLink";
import "./overlay.scss";

const Overlay = () => {
  const [open, setOpen] = useState(false);
  const openOverlay = () => {
    setOpen(true);
  };
  const closeOverlay = () => {
    setOpen(false);
  };
  const OverlayContent = ({ closeOverlay, open, openOverlay }) => {
    return (
      <div className="overlay-main">
        <OverlayLink openOverlay={openOverlay} />
        <div className={`overlay-content-wrapper ${open ? "show" : ""}`}>
          <div className="overlay-content">
            Lorem, ipsum dolor sit amet consectetur adipisicing elit. Fugit
            reprehenderit sequi, fugiat sapiente, incidunt suscipit maiores,
            laborum enim porro omnis ex vel debitis molestiae? Eveniet,
            corrupti. Consectetur qui adipisci esse.
            <div
              className="overlay-cancel"
              onClick={() => closeOverlay()}
            ></div>
          </div>
        </div>
      </div>
    );
  };
  console.log(open);
  return (
    <div>
      <OverlayContent
        open={open}
        closeOverlay={closeOverlay}
        openOverlay={openOverlay}
      />
    </div>
  );
};

export default Overlay;
