import React from "react";

const reducer = (state, action) => {
  console.log(state);
  switch (action.type) {
    case "add":
      return [
        ...state,
        {
          text: action.payload,
          id: Math.floor(Math.random() * 100),
        },
      ];
    case "sub":
      return { count: state.count - 1 };
    case "reset":
      return { count: 0 };
    default:
      break;
  }
};

export default reducer;
