import React from "react";

const Summary = ({ cartItems, addToCart }) => {
  return (
    <div className="summary-wrapper">
      <div className="cart-items">Cart Items</div>
      {cartItems &&
        cartItems.map((data, i) => {
          return (
            <div className="cart-items-info" key={i}>
              <div>{data.name}</div>
              <div className="cart-item-buttons">
                <button
                  className="button animation"
                  id={data.id}
                  onClick={(e) => addToCart(e)}
                >
                  +
                </button>
                <button
                  className="button animation"
                  id={data.id}
                  onClick={(e) => addToCart(e)}
                  data-remove="remove"
                >
                  -
                </button>
              </div>
              <div>{`${data.qty} x ${data.price}`}</div>
            </div>
          );
        })}
      {cartItems && <div className="summary">Summary</div>}
    </div>
  );
};

export default Summary;
