import React from "react";

const OverlayContent = ({
  closeOverlay,
  open,
  setOpen,
  directionClass = "",
}) => {
  const clickHandler = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setOpen(false);
    console.log(e.charCode);
    if (e.charCode === 27) {
      setOpen(false);
    }
  };
  return (
    <div
      onClick={(e) => clickHandler(e)}
      className={`overlay-content-wrapper ${open ? "show" : ""}`}
    >
      <div className={`overlay-content ${directionClass}`}>
        Lorem, ipsum dolor sit amet consectetur adipisicing elit. Fugit
