import React, { useRef } from "react";
import LeftScroll from "./leftScroll";
import RightScroll from "./rightScroll";
import "./syncScroll.scss";

const SyncScroll = () => {
  const ref = useRef();
  const handleScroll = (scroll) => {
    console.log(ref);
    const scroll1 = document.getElementById("scroll1");
    const scroll2 = document.getElementById("scroll2");
    if (scroll == "scroll1") {
      scroll2.scrollTop = scroll1.scrollTop;
    } else {
      scroll1.scrollTop = scroll2.scrollTop;
    }
  };
  return (
    <div className="parent-scroll">
      <LeftScroll handleScroll={handleScroll} />
      <RightScroll handleScroll={handleScroll} ref={ref} />
    </div>
  );
};

export default SyncScroll;
