import React from "react";

const reducer = (state, action) => {
  console.log(state);
  switch (action.type) {
    case "add":
      return [
        ...state,
        {
          text: action.payload,
          id: Math.floor(Math.random() * 100),
        },
      ];
    case "remove":
      return state.filter((todo) => todo.id !== action.id);
    case "remove":
      return state.filter((todo) => todo.id !== action.id);
    default:
      break;
  }
};

export default reducer;
