import React, { useState } from "react";
import "./overlay.scss";

const Overlay = () => {
  const [open, setOpen] = useState(false);
  const openOverlay = () => {
    setOpen(true);
  };
  const closeOverlay = () => {
    setOpen(false);
  };
  console.log(open);
  return (
    <div className="overlay-main">
      <button onClick={() => openOverlay()}>Open Overlay</button>
      <div className={`overlay-content-wrapper ${open ? "show" : ""}`}>
        <div className="overlay-content">
          Lorem, ipsum dolor sit amet consectetur adipisicing elit. Fugit
          reprehenderit sequi, fugiat sapiente, incidunt suscipit maiores,
          laborum enim porro omnis ex vel debitis molestiae? Eveniet, corrupti.
          Consectetur qui adipisci esse.
          <button className="overlay-cancel" onClick={() => closeOverlay()}>
            X
          </button>
        </div>
      </div>
    </div>
  );
};

export default Overlay;
