import React from "react";

const Summary = ({ cartItems, addToCart }) => {
  return (
    <div className="summary-wrapper">
      <div className="summary">Summary</div>
      <div className="cart-items">{`Cart Items ${
        cartItems ? cartItems : "Empty"
      }`}</div>
      {cartItems &&
        cartItems.map((data, i) => {
          return (
            <div className="cart-items-info" key={i}>
              <div>{data.name}</div>
              <button
                className="button animation"
                id={data.id}
                onClick={(e) => addToCart(e)}
              >
                +
              </button>
              <button
                className="button animation"
                id={data.id}
                onClick={(e) => addToCart(e)}
                data-remove="remove"
              >
                -
              </button>
              <div>{`${data.qty} x ${data.price}`}</div>
            </div>
          );
        })}
    </div>
  );
};

export default Summary;
