import React, { useState } from "react";

const Products = ({ products, setCartItems, cartItems }) => {
  const [checkItem, setCheckItem] = useState();
  const addToCart = (e) => {
    const isExist = cartItems.find((data) => data.id === e.target.id);

    setCheckItem(
      cartItems.map((item) => {
        if (item.id === e.target.id) {
          return { ...item, qty: item.qty + 1 };
        } else {
          return item;
        }
      })
    );
    // console.log(isExist, "isExist");

    products.products.map((data) => {
      if (e.target.id === data.id && !isExist) {
        return setCartItems([...cartItems, data]);
      }
    });
    isExist &&
      setCartItems(
        cartItems.map((item) => {
          if (e.target.id === item.id) {
          }
        })
      );
  };
  //   console.log(checkItem);
  return (
    <div className="products-wrapper">
      {products &&
        products.products.map((product, i) => {
          return (
            <div className="products" key={i}>
              <img src={product.img} alt="" />
              <div>
                <div className="prop">{product.name}</div>
                <div className="prop">{product.color}</div>
                <div className="prop">${product.price}</div>
                <button
                  onClick={(e) => addToCart(e)}
                  className="animation button"
                  id={product.id}
                >
                  Add to cart
                </button>
              </div>
            </div>
          );
        })}
    </div>
  );
};

export default Products;
