import React from "react";

const reducer = (state, action) => {
  switch (action.type) {
    case "add":
      return [
        ...state,
        {
          text: action.payload,
          id: Math.floor(Math.random() * 1000),
        },
      ];
    default:
      break;
  }
};

export default reducer;
