import React, { useState, useEffect } from "react";
import "./getRandomUser.scss";
import Loading from "../loading/loading";

const GetRandomUser = () => {
  const [user, setUser] = useState([]);
  const [results, setResults] = useState([]);

  const fetchNextUser = (page = 1) => {
    fetch(`https://randomuser.me/api?page=${page}`)
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setUser(data);
        const newResults = [...results, ...data.results];
        setResults(newResults);
      });
  };

  useEffect(() => {
    fetchNextUser();
  }, []);

  // const debounce = (fn, delay) => {
  //   let setTime;
  //   return function (...arg) {
  //     if (setTime) {
  //       clearTimeout(setTime);
  //     }
  //     setTime = setTimeout(() => {
  //       fn(...arg);
  //     }, delay);
  //   };
  // };

  const throttle = (func, limit) => {
    let lastFunc;
    let lastRan;
    return function () {
      const context = this;
      const args = arguments;
      if (!lastRan) {
        func.apply(context, args);
        lastRan = Date.now();
      } else {
        clearTimeout(lastFunc);
        lastFunc = setTimeout(function () {
          if (Date.now() - lastRan >= limit) {
            func.apply(context, args);
            lastRan = Date.now();
          }
        }, limit - (Date.now() - lastRan));
      }
    };
  };

  return user ? (
    <div className="user-data">
      {results.length !== 0 ? (
        results.map((user) => {
          return (
            <div className="user-info animation">
              <div>Name: {user.name.first}</div>
              <img src={user.picture.thumbnail} alt="" />
            </div>
          );
        })
      ) : (
        <Loading text="Loading" />
      )}

      <button
        onClick={throttle(() => {
          fetchNextUser();
        }, 3000)}
        className="data-button animation"
      >
        Get new user
      </button>
    </div>
  ) : (
    ""
  );
};

export default GetRandomUser;
