import React, { useState, useRef } from "react";
import { Link, Route } from "react-router-dom";

import TextField from "../textField/textField";
import Button from "../button/button";
import "./login.scss";

const SignUp = () => {
  return <div>Sign Up</div>;
};

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailVaild, setEmailVaild] = useState(false);
  const [passwordValid, setPasswordValid] = useState(false);
  const [errorMessage, setErrorMessage] = useState("Please enter Email");

  const onBlur = (e) => {
    if (e.target.value === "") {
      setPasswordValid(true);
    } else {
      setPasswordValid(false);
    }
  };

  const emailValid = (e) => {
    const pattern = new RegExp(
      /^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i
    );
    e.target.id === "email" &&
      e.target.value !== "" &&
      !pattern.test(e.target.value) &&
      setErrorMessage("Please enter valid Email");
    if (
      e.target.id === "email" &&
      e.target.value !== "" &&
      pattern.test(e.target.value)
    ) {
      setEmailVaild(false);
    } else if (e.target.value === "") {
      setEmailVaild(true);
    }
  };
  const ref = useRef(null);
  const validation = {
    onClick: (e) => {
      event.preventDefault();
      if (email && password) {
        return;
      } else {
        setPasswordValid(true);
        setEmailVaild(true);
        ref.current.focus();
      }
    },
  };

  return (
    <div className="login-wrapper fade-in">
      <div className="login-fields">
        <div className="login-title">Enter Login Details</div>
        <form action="post" onSubmit={validation()}>
          <TextField
            inputLabel="Email"
            id="email"
            ref={ref}
            setInput={setEmail}
            input={email}
            type="email"
            onBlur={emailValid}
            error={emailVaild}
            errorMessage={errorMessage}
          />
          <TextField
            id="password"
            inputLabel="Password"
            setInput={setPassword}
            input={password}
            type="password"
            onBlur={onBlur}
            error={passwordValid}
            errorMessage="Please enter Password"
          />
        </form>

        <div className="login-buttons">
          <Button handlers={validation} label="Login" />
          <Link to={`/login/signin`}>
            <Button buttonLink label="Forgot Password" />
          </Link>
        </div>
      </div>
      <Route path="/login/signin" component={SignUp} />
    </div>
  );
};

export default Login;
