import React, { useState, useRef, useEffect } from "react";
import { Link, Route, useLocation } from "react-router-dom";

import TextField from "../textField/textField";
import Button from "../button/button";
import "./login.scss";

const SignUp = () => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [reEnterPassword, setReEnterPassword] = useState("");
  const [dob, setDOB] = useState("");
  const inputAttrs = {
    "data-parse": "date",
    placeholder: "MM/DD/YYYY",
    pattern: "d{2}/d{2}/d{4}",
    required: "required",
    onBlur: (e) => {
      setDOB(e.target.value.charAt(0).toUpperCase() + e.target.value.slice(1));
      let input = e.target.value;
      const [month, day, year] = input.split("/");
      console.log(month, day, year);
      return setDOB(`${year}-${month}-${day}`);
    },
  };

  const dobChange = (e) => {
    // setDOB(e.target.value.charAt(0).toUpperCase() + e.target.value.slice(1));
    // let input = e.target.value;
    // const [month, day, year] = input.split("/");
    // console.log(`${year}-${month}-${day}`);
    // return setDOB(`${year}-${month}-${day}`);
  };

  return (
    <>
      <div className="login-wrapper fade-in enable">
        <div className="login-fields enable">
          <div className="login-title">Enter SignUp Details</div>
          <form>
            <TextField
              inputLabel="FirstName"
              id="firstName"
              setInput={setFirstName}
              input={firstName}
              onBlur={() => null}
              type="text"
            />
            <TextField
              inputLabel="LastName"
              id="lastName"
              onBlur={() => null}
              setInput={setLastName}
              input={lastName}
              type="text"
            />
            <TextField
              inputLabel="Email Address"
              id="email"
              setInput={setEmail}
              onBlur={() => null}
              input={email}
              type="email"
            />
            <TextField
              inputLabel="DOB"
              id="dob"
              inputAttrs={inputAttrs}
              setInput={dobChange}
              input={dob}
              type="text"
            />
            <TextField
              inputLabel="Password"
              id="password"
              setInput={setPassword}
              input={password}
              onBlur={() => null}
              type="password"
            />
            <TextField
              inputLabel="Re-Enter Passord again"
              id="reenterpassword"
              setInput={setReEnterPassword}
              onBlur={() => null}
              input={reEnterPassword}
              type="password"
            />
            <div className="login-buttons">
              <Button label="Login" type="submit" />
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailVaild, setEmailVaild] = useState(false);
  const [passwordValid, setPasswordValid] = useState(false);
  const [errorMessage, setErrorMessage] = useState("Please enter Email");
  const [disable, setDisable] = useState(false);

  let location = useLocation();
  console.log(location === "/login");
  useEffect(() => {
    if (location.pathname !== "/login") {
      setDisable(true);
    } else {
      setDisable(false);
    }
  }, [location.pathname]);

  const onBlur = (e) => {
    if (e.target.value === "") {
      setPasswordValid(true);
    } else {
      setPasswordValid(false);
    }
  };

  const emailValid = (e) => {
    const pattern = new RegExp(
      /^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i
    );
    e.target.id === "email" &&
      e.target.value !== "" &&
      !pattern.test(e.target.value) &&
      setErrorMessage("Please enter valid Email");
    if (
      e.target.id === "email" &&
      e.target.value !== "" &&
      pattern.test(e.target.value)
    ) {
      setEmailVaild(false);
    } else if (e.target.value === "") {
      setEmailVaild(true);
    }
  };
  const ref = useRef(null);
  const validation = (e) => {
    e.preventDefault();
    if (email && password) {
      const data = new FormData(e.target);
      console.log(data, e.target);
      fetch("/api/form-submit-url", {
        method: "POST",
        body: data,
      });
    } else {
      setPasswordValid(true);
      setEmailVaild(true);
      ref.current.focus();
    }
  };

  return (
    <div className="login-wrapper fade-in">
      <div className={`login-fields ${disable ? "disable" : ""}`}>
        <div className="login-title">Enter Login Details</div>
        <form onSubmit={(e) => validation(e)}>
          <TextField
            inputLabel="Email"
            id="email"
            ref={ref}
            setInput={setEmail}
            input={email}
            type="email"
            onBlur={emailValid}
            error={emailVaild}
            disabled={disable}
            errorMessage={errorMessage}
          />
          <TextField
            id="password"
            inputLabel="Password"
            setInput={setPassword}
            input={password}
            type="password"
            disabled={disable}
            onBlur={onBlur}
            error={passwordValid}
            errorMessage="Please enter Password"
          />
          <div className="login-buttons">
            <Button label="Login" type="submit" disabled={disable} />

            <Button buttonLink label="Forgot Password" disabled={disable} />
          </div>
        </form>
        <div className="new-user">
          <span>New User?</span>
          <Link
            to={`/login/newuser`}
            onClick={() => {
              if (location !== "/login") setDisable(true);
            }}
          >
            <Button buttonLink label="Sign Up" disabled={disable} />
          </Link>
        </div>
      </div>

      <Route path="/login/newuser" component={SignUp} />
    </div>
  );
};

export default Login;
