import React from "react";

const OverlayContent = ({
  closeOverlay,
  open,
  setOpen,
  directionClass = "",
}) => {
  const inputAttrs = {
    onClick: (event) => {
      event.preventDefault();
      event.stopPropagation();
      setOpen(false);
      if (event.charCode === 27) {
        setOpen(false);
      }
    },
  };
  return (
    <div
      {...inputAttrs}
      className={`overlay-content-wrapper ${open ? "show" : ""}`}
    >
      <div className={`overlay-content ${directionClass}`}>
        Lorem, ipsum dolor sit amet consectetur adipisicing elit. Fugit
        reprehenderit sequi, fugiat sapiente, incidunt suscipit maiores, laborum
        enim porro omnis ex vel debitis molestiae? Eveniet, corrupti.
        Consectetur qui adipisci esse.
        <div className="overlay-cancel" onClick={() => closeOverlay()}></div>
      </div>
    </div>
  );
};

export default OverlayContent;
