import React, { useState } from "react";
import OverlayLink from "./overlayLink";
import OverlayContent from "./overlayLink";
import "./overlay.scss";

const Overlay = () => {
  const [open, setOpen] = useState(false);
  const openOverlay = () => {
    setOpen(true);
  };
  return (
    <>
      <OverlayLink setOpen={setOpen} />
      <OverlayContent setOpen={setOpen} open={open} />
    </>
  );
};

export default Overlay;
